import React, { useState } from "react";

const TheatreSeatGenerator = ({ seatData, rowLabel, seatType, bookedSeats }) => {
  const [selectedSeats, setSelectedSeats] = useState([]); // State for selected seats

  // Handle seat selection and deselection
  const handleSeatClick = (seat) => {
    const seatId = `${rowLabel}${seat}`; // Unique seat identifier (e.g., A1, B5)

    if (bookedSeats.includes(seatId)) return; // Prevent selection of booked seats

    if (selectedSeats.includes(seatId)) {
      setSelectedSeats(selectedSeats.filter((s) => s !== seatId)); // Remove seat
    } else {
      setSelectedSeats([...selectedSeats, seatId]); // Add seat
    }
  };

  // Determine seat color based on availability
  const getSeatColor = (seat) => {
    const seatId = `${rowLabel}${seat}`;
    if (bookedSeats.includes(seatId)) return "red"; // Booked seat
    if (selectedSeats.includes(seatId)) return "#21a0e9"; // Selected seat
    return "#fff"; // Available seat
  };

  // Render a single seat
  const renderSeat = (seat) => (
    <div
      key={`${rowLabel}${seat}`}
      className='theater'
      style={{
        backgroundColor: getSeatColor(seat),
        cursor: bookedSeats.includes(`${rowLabel}${seat}`) ? "not-allowed" : "pointer",
      }}
      onClick={() => handleSeatClick(seat)}
    >
      {rowLabel}{seat}
    </div>
  );

  return (
    <div className="seat-row">
      {seatData?.map((seat) => renderSeat(seat))}
    </div>
  );
};

export default TheatreSeatGenerator;
