import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const TheatreSelection = () => {
  const { title } = useParams();
  const [theatres, setTheatres] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTheatres = async () => {
      try {
        console.log(`Fetching theatres for: ${title}`); // Debug log
        const response = await fetch(`/api/movies/${encodeURIComponent(title)}/theatres`);
        
        if (!response.ok) {
          throw new Error(`HTTP Error! Status: ${response.status}`);
        }

        const data = await response.json();
        console.log("Fetched theatres:", data); // Debug log
        setTheatres(data.theatres);
      } catch (err) {
        console.error("Error fetching theatres:", err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchTheatres();
  }, [title]);

  if (loading) return <p>Loading theatres...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div>
      <h2>Theatres Showing {title}</h2>
      {theatres.length > 0 ? (
        <ul>
          {theatres.map((theatre, index) => (
            <li key={index}>
              <strong>{theatre.name}</strong> - {theatre.location} <br />
              Showtimes: {theatre.showTimes?.join(", ") || "No showtimes available"}
            </li>
          ))}
        </ul>
      ) : (
        <p>No theatres found.</p>
      )}
    </div>
  );
};

export default TheatreSelection;
