import React, { useEffect, useState } from 'react';
import Controller from '../../hooks/controller';
import { useNavigate } from 'react-router-dom';
import { IoMdAdd } from 'react-icons/io';
import { CgTrash } from 'react-icons/cg';
import { MdEditSquare } from 'react-icons/md';
import { RiSearchLine } from 'react-icons/ri';

function Movie() {
  const { movies = [], deleteMovie, setEditMovie, searchMovie, getMovies } = Controller();

  const navigate = useNavigate();
  const [movieTitle, setMovieTitle] = useState('');

  // üîπ Debugging: Log movies on every update
  useEffect(() => {
    console.log("Updated movie list:", movies);
  }, [movies]);

  // üîπ Search function with better logging
  const search = async () => {
    const data = { title: movieTitle };
    console.log("Searching for movie:", data);
    
    try {
      const result = await searchMovie(data);
      console.log("Search result:", result);
    } catch (error) {
      console.error("Error in searchMovie:", error);
    }
  };

  // üîπ Fetch all movies when the page loads or movieTitle is empty
  useEffect(() => {
    if (movieTitle.length === 0) {
      console.log("Fetching all movies...");
      getMovies();
    }
  }, [movieTitle]);

  return (
    <div className='movie'>
      <h2 className='admin-title'>Admin Page</h2>
      <div className='movieHeader'>
        <div className='adsearch'>
          <label htmlFor='movieTitle'>Movie: </label>
          <input
            type='text'
            id='movieTitle'
            placeholder='Movie title'
            value={movieTitle}
            onChange={(e) => setMovieTitle(e.target.value)}
          />
          <button type='submit' className='aSearch' onClick={search}>
            <RiSearchLine size={30} />
          </button>
        </div>
        <button className='create' onClick={() => navigate('/adminhome/createmovie')}>
          New Movie <IoMdAdd size={20} />
        </button>
      </div>

      {/* üîπ Check if movies exist */}
      {movies.length > 0 ? (
        movies.map((movie, index) => {
          console.log("Rendering movie:", movie.title); // Debugging to check if the map function is working
          return (
            <div key={index} className='movieItem'>
              <div className='info'>
                <div>üé¨ {movie.title}</div>
                <div>üé≠ Genre: {movie.genre}</div>
                <div>‚è≥ Duration: {movie.duration}</div>
                <div>üó£Ô∏è Language: {movie.language}</div>
                <div>üìÖ Release: {movie.releaseDate}</div>
                <div>‚≠ê Rating: {movie.rating}</div>
                <div>üè¢ Theatre: {movie.theatre}</div>

                {/* üõ† Wrapped buttons inside .row div */}
                <div className='row'>
                  <button
                    className='edit'
                    onClick={() => {
                      setEditMovie(movie);
                      navigate('/adminhome/EditMovie');
                    }}
                  >
                    Edit <MdEditSquare size={25} />
                  </button>
                  <button className='delete' onClick={() => deleteMovie(movie._id)}>
                    Delete <CgTrash size={25} />
                  </button>
                </div>
              </div>
            </div>
          );
        })
      ) : (
        <p>No movies found</p>
      )}
    </div>
  );
}

export default Movie;
