import React, {useState} from 'react'
import useSearch from '../hooks/controller'
import '../style/search.css'

function Search() {
    const {SearchTheatre, SearchMovie} = useSearch()
    const [searchQuery, setSearchQuery] = useState('')
    const [isSearchingForMovie] = useState(false);
    const [isSearchingForTheatre] = useState(false);

    const handleTheatreSearch = () => {
        if (searchQuery.trim() === '') {
            alert("Enter a theatre name to search!");
            return;
        }
        SearchTheatre({ theatreName: searchQuery });
    };
    
    const handleMovieSearch = async () => { // Make it async to wait for movie data
        if (searchQuery.trim() === '') {
            alert("Enter a movie name to search!");
            return;
        }
        const movieData = await SearchMovie({ title: searchQuery });
    
        // ✅ Only search for theatres if the movie has associated theatres
        if (movieData?.theatres?.length > 0) {
            SearchTheatre({ theatreName: movieData.theatres });
        }
    };
    
    const handleSearch = () => {
        if (isSearchingForMovie) {  // If searching for a movie
            handleMovieSearch();
        } else if (isSearchingForTheatre) {  // If searching for a theatre
            handleTheatreSearch();
        } else {  // Default: If user enters a generic query, check both
            handleMovieSearch();
            handleTheatreSearch();
        }
    };
    
    
  return (
    <div className='search-container'>
        <h2>Search for Theatre or Movie</h2>
        <input type='text' placeholder='Enter theatre or movie name' value={searchQuery} onChange={(e)=>setSearchQuery(e.target.value)}/>
        <button onClick={handleSearch}>Search</button>
    </div>
  )
}

export default Search
