import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Controller from "../hooks/Controller";
import "../style/login.css";

function Login() {
  const [useremail, setUserEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const { login } = Controller();

  // Handle login process
  const handleLogin = async () => {
    if (!useremail || !password) {
      alert("Please enter both email and password.");
      return;
    }

    // Send login request
    const result = await login({ email: useremail, password });

    if (!result || !result.success) {
      console.error("Login failed:", result?.message || "Unknown error");
      alert(result?.message || "Login failed. Please try again.");
    } else {
      console.log("Login successful:", result.data);

      // Navigate based on role
      if (result.data.role === "admin") {
        navigate("/adminhome");
      } else {
        navigate("/userhome");
      }
    }
  };

  return (
    <div className="loginbody">
      <div className="loginbox">
        <div className="login">
          <h2>Login</h2>
        </div>
        <div className="logindetails">
          <div className="userNameoremail">
            <label>UserEmail: </label>
            <input
              type="text"
              required
              placeholder="Enter userEmail"
              value={useremail}
              onChange={(e) => setUserEmail(e.target.value)}
              autoComplete="off"
            />
          </div>
          <div className="loginpassword">
            <label>Login Password:</label>
            <input
              type="password"
              required
              placeholder="Enter Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
               autoComplete="off"
            />
          </div>
          <div className="logsubmit">
            <button className="log" onClick={handleLogin}> {/* âœ… Fixed */}
              Submit
            </button>
            <p onClick={() => navigate("/signup")}>Create account</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
