import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "../style/TheatreSelect.css";
import api from "../api/backend";

const TheatreSelect = () => {
  const { title } = useParams();
  const navigate = useNavigate();
  const [theatres, setTheatres] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [selectedDate, setSelectedDate] = useState(getFormattedDate(0)); // Default to today

  useEffect(() => {
    const fetchTheatres = async () => {
      try {
        const decodedTitle = decodeURIComponent(title);
        console.log("Fetching theatres for:", decodedTitle, "on date:", selectedDate);

        const response = await fetch(
          `http://localhost:3800/api/movies/${decodedTitle}/theatres?date=${selectedDate}`
        ,{method:''});

        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const data = await response.json();
        console.log("Fetched Theatres:", data);

        setTheatres(data.theatres || []);
      } catch (err) {
        console.error("Error fetching theatres:", err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchTheatres();
  }, [title, selectedDate]); 

  const handleShowtimeClick = async (theatre, showtime) => {
    try {
      console.log("Fetching seat details for theatre:", theatre._id);
      
      // Validate theatre ID
      if (!theatre._id || typeof theatre._id !== 'string') {
        throw new Error("Invalid theatre ID");
      }
  
      // Fetch seat details from backend
      const seatResponse = await api.get(`/api/seat-details/${theatre._id}`);
      
      // Handle non-OK responses
      if (seatResponse.status !== 200) {
        throw new Error(seatResponse.data?.error || 'Failed to fetch seat details');
      }
  
      const seatData = seatResponse.data;
      
      // Merge theatre data with seat details
      const theatreWithSeats = {
        ...theatre,
        showTime: showtime,
        availableSeats: seatData.availableSeats || {},  
        bookedSeats: seatData.bookedSeats || []  
      };
  
      navigate("/TheatreLayout", { state: { theatreData: theatreWithSeats } });
      
    } catch (error) {
      console.error("Error fetching seat details:", error);
      setError(error.message);
    }
  };
  

  return (
    <div className="theatre-container">
      {error && (
      <div className="error-message">
        Error: {error}
        <button onClick={() => setError("")}>Dismiss</button>
      </div>
      )}
      <h2>Available Theatres for {decodeURIComponent(title)}</h2>

      {/* Date Selector */}
      <div className="date-selector">
        {getNext7Days().map((date) => (
          <button
            key={date}
            className={`date-button ${selectedDate === date ? "selected" : ""}`}
            onClick={() => setSelectedDate(date)}
          >
            {formatDate(date)}
          </button>
        ))}
      </div>

      {loading && <p>Loading theatres...</p>}
      {error && <p>Error: {error}</p>}
      {!loading && !error && theatres.length === 0 && <p>No theatres found for this date.</p>}
      
      {!loading && !error &&
        theatres.map((theatre, index) => (
          <div key={index} className="theatre-box">
            <div className="theatre-info">
              <h3 className="theatre-name">{theatre.name}</h3>
              <p className="theatre-location">{theatre.location}</p>
            </div>
            <div className="showtimes">
              {theatre.showTimes.map((showtime, idx) => (
                <button
                  key={idx}
                  className="showtime-btn"
                  onClick={() => handleShowtimeClick(theatre)}
                >
                  {showtime}
                </button>
              ))}
            </div>
          </div>
        ))
      }
    </div>
  );
};

// Helper function: Get next 7 days
const getNext7Days = () => {
  const dates = [];
  for (let i = 0; i < 7; i++) {
    const date = new Date();
    date.setDate(date.getDate() + i);
    dates.push(date.toISOString().split("T")[0]); // YYYY-MM-DD format
  }
  return dates;
};

// Helper function: Format date like "Sun 02 Mar"
const formatDate = (dateString) => {
  const date = new Date(dateString);
  return date.toLocaleDateString("en-US", {
    weekday: "short",
    day: "2-digit",
    month: "short",
  });
};

const getFormattedDate = (offset) => {
  const date = new Date();
  date.setDate(date.getDate() + offset);
  return date.toISOString().split("T")[0]; 
};

export default TheatreSelect;
