import React, { useState } from 'react';
import { useTheatreList } from '../hooks/controller'; 
import "../"

function CreateTheatre() {
    const [theatreName, setTheatreName] = useState('');
    const [location, setLocation] = useState('');
    const [screens, setScreens] = useState('');
    const [capacity, setCapacity] = useState('');
    const [facilities, setFacilities] = useState('');
    const [image, setImage] = useState(null);

    const { CreateTheatre } = useTheatreList();

    const handleCreateTheatre = () => {
        const theatreData = {
            theatreName,
            location,
            screens,
            capacity,
            facilities,
            image
        };

        CreateTheatre(theatreData);
    };

    return (
        <div className='new-theatre'>
            <h2>Create Theatre</h2>

            <div>
                <label>Theatre Name:</label>
                <input type='text' value={theatreName} onChange={(e) => setTheatreName(e.target.value)} placeholder='Enter theatre name' />
            </div>

            <div>
                <label>Location:</label>
                <input type='text' value={location} onChange={(e) => setLocation(e.target.value)} placeholder='Enter location' />
            </div>

            <div>
                <label>Number of Screens:</label>
                <input type='number' value={screens} onChange={(e) => setScreens(e.target.value)} placeholder='Enter number of screens' />
            </div>

            <div>
                <label>Seating Capacity:</label>
                <input type='number' value={capacity} onChange={(e) => setCapacity(e.target.value)} placeholder='Enter seating capacity' />
            </div>

            <div>
                <label>Facilities:</label>
                <textarea value={facilities} onChange={(e) => setFacilities(e.target.value)} placeholder='Enter facilities (e.g., IMAX, Dolby Atmos, 3D)' />
            </div>

            <div>
                <label>Theatre Image:</label>
                <input type='file' accept='image/*' onChange={(e) => setImage(e.target.files[0])} />
            </div>

            <button className='save-btn' onClick={handleCreateTheatre}>Create Theatre</button>
        </div>
    );
}

export default CreateTheatre;
