import React, { useState } from "react";
import "../style/TheatreLayout.css";

const TheatreSeatGenerator = ({ seatData, rowLabel, seatType, bookedSeats, onSeatSelect }) => {
  const [selectedSeats, setSelectedSeats] = useState([]);

  const handleSeatClick = (seatNumber) => {
    if (bookedSeats.includes(seatNumber)) return; // Ignore clicks on booked seats

    setSelectedSeats((prevSelected) =>
      prevSelected.includes(seatNumber)
        ? prevSelected.filter((seat) => seat !== seatNumber) // Deselect if already selected
        : [...prevSelected, seatNumber] // Select new seat
    );

    onSeatSelect(seatNumber);
  };

  return (
    <div className={`seat-row ${seatType}`}>
      {seatData.map((seatNumber) => (
        <button
          key={seatNumber}
          className={`seat 
            ${bookedSeats.includes(seatNumber) ? "booked" : ""}
            ${selectedSeats.includes(seatNumber) ? "selected" : ""}`
          }
          onClick={() => handleSeatClick(seatNumber)}
        >
          {rowLabel}{seatNumber}
        </button>
      ))}
    </div>
  );
};

export default TheatreSeatGenerator;
