import { useState, useEffect } from 'react';
import { Navigate, useNavigate, useParams } from 'react-router-dom';
import api from '../api/backend';

const Controller = () => {
  const Navigate = useNavigate(); 
  const [userDetail, setUserDetail] = useState(null)
  const [userLogin, setUserLogin] = useState(false)
  const [isAdmin, setAdmin] = useState(false)
  const [searchResults, setSearchResults] = useState([])
  const [searchDetails, setSearchDetails] = useState(null)
  const [movie, setMovie] = useState(null)
  const[loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [selectedTheatre, setSelectedTheatre] = useState(null)
  const [selectedShowtime, setSelectedShowtime] = useState(null)
  const { movieId } = useParams()

  const signup = async (user) => {
    try {
      console.log('User Data:', user);

      const response = await api.post('/register', user);

      console.log('Response:', response.data.message);

      if (response.data.permission !== true) { 
        alert(response.data.message);
      } else {
        alert('Signup Successful!');
        Navigate('/login'); 
      }
    } catch (err) {
      if (err.response) {
        console.error('Error Response:', err.response.data);
        alert(err.response.data.message || 'Signup failed');
      } else {
        console.error(`ERROR: ${err.message}`);
        alert('Something went wrong. Please try again.');
      }
    }
  };
  const findUser = ()=>{
    try {
        const user = localStorage.getItem("userdetail")
        if(user){
            setUserDetail(JSON.parse(user))
            setUserLogin(true)
        }else{
            setUserLogin(false)
        }
    } catch (error) {
        console.error("Error retrieving user:", error)
    }
  }

  const saveUser = (user)=>{
    try {
        localStorage.setItem("userdetail", JSON.stringify(user))
        setUserDetail(user)
    } catch (error) {
        console.error("Error saving user:", error)
    }
  }

  const saveToken = (token)=>{
    try {
        localStorage.setItem("usertoken", token)
    } catch (error) {
        console.error("Error saving token", error)
    }
  }

  const login = async(userData)=>{
    try {
        const response = await api.post("user/login", userData)
        if(response?.data?.token){
            saveToken(response.data.token)
            saveUser(userData)
            findUser()
            
            if(response?.data?.role === "admin"){
                Navigate("/adminhome")
                setAdmin(true)
            }else{
                setAdmin(false)
                Navigate('/search')
            }
        }
    } catch (error) {
        setUserLogin(false)
        console.error("Login Error:", error.response ? error.response.data: error.message)

    }
  }
  useEffect(()=>{
    findUser();
  }, [])
  const SearchTheatre = async(theatreData)=>{
    try {
        const response = await api.get('api/search', theatreData)
        console.log("Theatre search Response", response.data)

        if(response.data && response.data.length > 0){
            setSearchResults(response.data)
            setSearchDetails(theatreData)
            Navigate('/theatre-results')
        }else{
            alert("No theatres found")
        }
    } catch (error) {
        alert("search failed. please try again")
    }
  }
  
  const SearchMovie = async(movieData)=>{
    try {
        const response = await api.get(`/api/movies/name/${movieData}`)
        console.log("Movie search Response", response.data)
        if(response.data && response.data.length > 0){
            setSearchResults(response.data.movie)
            setSearchDetails(movieData)
            Navigate('/movie-results')
        }else{
            alert("no movies found")
        }
    } catch (error) {
        alert("search failed. please try again")
    }
  }
  useEffect(()=>{
    const fetchMovie = async () => {
      try {
        const response = await fetch(`http://localhost:3800/api/movies/name/${movieId}`); // Ensure movieId contains the actual name
        if (!response.ok) throw new Error("Failed to fetch movie details");
        const data = await response.json();
        setMovie(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };    
    fetchMovie()
  }, [movieId])

  const  selectTheatre = (theatre)=>{
    setSelectedTheatre(theatre)
    setSelectedShowtime(null)
  }
  const selectShowtime = (time)=>{
    setSelectedShowtime(time)
  }
  return { signup,  userDetail, userLogin, isAdmin, login, SearchTheatre, SearchMovie, searchDetails, searchResults,
    movie, loading,error, selectShowtime,selectTheatre
  };
};


export default Controller;
