import { useState, useEffect, createContext, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/backend';

const Controller = () => {
  const navigate = useNavigate();
  const [userDetail, setUserDetail] = useState(null);
  const [userLogin, setUserLogin] = useState(false);
  const [isAdmin, setAdmin] = useState(false);
  const [searchResults, setSearchResults] = useState([]);
  const [searchDetails, setSearchDetails] = useState(null);
  const [TheatreResult, setTheatreResults] = useState([]);
  const [selectedTheatre, setSelectedTheatre] = useState(null);
  const [selectedShowtime, setSelectedShowtime] = useState(null);

  const signup = async (user) => {
    try {
      console.log('User Data:', user);
      const response = await api.post('/register', user);
      console.log('Response:', response.data.message);

      if (response.data.permission !== true) {
        alert(response.data.message);
      } else {
        alert('Signup Successful!');
        navigate('/login');
      }
    } catch (err) {
      alert(err.response?.data?.message || 'Signup failed');
      console.error('Error:', err);
    }
  };

  const findUser = () => {
    try {
      const user = localStorage.getItem("userdetail");
      if (user) {
        setUserDetail(JSON.parse(user));
        setUserLogin(true);
      } else {
        setUserLogin(false);
      }
    } catch (error) {
      console.error("Error retrieving user:", error);
    }
  };

  const saveUser = (user) => {
    try {
      localStorage.setItem("userdetail", JSON.stringify(user));
      setUserDetail(user);
    } catch (error) {
      console.error("Error saving user:", error);
    }
  };

  const saveToken = (token) => {
    try {
      localStorage.setItem("usertoken", token);
    } catch (error) {
      console.error("Error saving token", error);
    }
  };


  const login = async (userData) => {
    try {
        const response = await api.post('/auth/user/login', userData);
        console.log("API Response:", response.data); // Debug log

        // Handle token and user data
        savetoken(response.data.token);
        saveuser(userData);

        // Role-based navigation
        if (response.data.role === 'admin') {
            setadmin(true);
            Navigate('/adminhome');
        } else {
            setadmin(false);
            Navigate(-1);
        }

        return { success: true, data: response.data };
    } catch (err) {
        console.error("Login error:", err);

        // Return a structured error message
        return { success: false, message: err.response?.data?.message || "Network error. Please try again." };
    }
};


  useEffect(() => {
    findUser();
  }, []);


  const SearchTheatre = async (theatreData) => {
    try {
      const response = await api.get('api/search', theatreData);
      console.log("Theatre search Response", response.data);

      if (response.data && response.data.length > 0) {
        setSearchResults(response.data);
        setSearchDetails(theatreData);
        navigate('/theatre-results');
      } else {
        alert("No theatres found");
      }
    } catch (error) {
      alert("Search failed. Please try again.");
    }
  };


  const SearchMovie = async (movieData) => {
    try {
      const response = await api.get(`/api/movies/name/${movieData}`);
      console.log("Movie search Response", response.data);
      if (response?.data?.movie?.length > 0) {
        setSearchResults(response.data.movie);
        setSearchDetails(movieData);
        navigate('/MovieResult', { state: { movies: response.data.movie } });
      } else {
        console.log("No movies found.");
      }
    } catch (error) {
      console.log(error);
    }
  };


  const SearchTheatres = async (title) => {
    try {
      const response = await api.get(`/api/movies/${title}/theatres`);
      console.log("Theatre search Response", response.data);

      if (response?.data?.theatres?.length > 0) {
        setTheatreResults(response.data.theatres);
        setSearchDetails(title);
        navigate('/TheatreResult', { state: { theatres: response.data.theatres } });
      } else {
        console.log("No theatres found for this movie.");
      }
    } catch (error) {
      console.log("Error fetching theatre data:", error);
    }
  };


  const selectTheatre = (theatre) => setSelectedTheatre(theatre);
  const selectShowtime = (time) => setSelectedShowtime(time);

  return {
    signup, userDetail, userLogin, isAdmin, login,
    SearchTheatre, SearchMovie, searchDetails, searchResults,
    selectedTheatre, selectedShowtime, SearchTheatres,
    selectTheatre, selectShowtime
  };
};


const MovieContext = createContext();
const useMovieList = () => useContext(MovieContext);

const MovieProvider = ({ children }) => {
  const [movies, setMovies] = useState([]);

  const getMovies = async () => {
    try {
      const response = await fetch("http://localhost:3800/api/movies");
      const data = await response.json();
      setMovies(data);
    } catch (error) {
      console.error("Error fetching movies:", error);
    }
  };

  const deleteMovie = async (id) => {
    try {
      await fetch(`http://localhost:3800/api/movies/${id}`, { method: "DELETE" });
      setMovies(movies.filter(movie => movie._id !== id));
    } catch (error) {
      console.error("Error deleting movie:", error);
    }
  };

  return (
    <MovieContext.Provider value={{ movies, getMovies, deleteMovie }}>
      {children}
    </MovieContext.Provider>
  );
};

export { MovieProvider, useMovieList };
export default Controller;
