// TheaterSelectionPage.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const TheaterSelectionPage = () => {
  const [theaters, setTheaters] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { movieName } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTheaters = async () => {
      try {
        setLoading(true);
        // Make sure this endpoint matches your backend route
        const response = await axios.get(`/api/theatre/movie/${encodeURIComponent(movieName)}`);
        
        // Check the structure of your response based on your backend
        const theatresData = response.data.theatres || [];
        setTheaters(theatresData);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching theaters:', err);
        setError('Failed to load theaters. Please try again.');
        setLoading(false);
      }
    };

    if (movieName) {
      fetchTheaters();
    }
  }, [movieName]);

  const handleTheaterSelect = (theaterName) => {
    // Navigate to the seat selection page
    navigate(`/booking/${encodeURIComponent(movieName)}/${encodeURIComponent(theaterName)}`);
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading theaters...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <h2>Error</h2>
        <p>{error}</p>
        <button 
          className="back-button"
          onClick={() => navigate(-1)}
        >
          Go Back
        </button>
      </div>
    );
  }

  return (
    <div className="theater-selection-container">
      <h1 className="page-title">Select a Theater</h1>
      <h2 className="movie-name">{movieName}</h2>
      
      {theaters.length === 0 ? (
        <div className="no-theaters">
          <p>No theaters available for this movie.</p>
          <button 
            className="back-button"
            onClick={() => navigate(-1)}
          >
            Back to Movie
          </button>
        </div>
      ) : (
        <>
          <div className="theaters-grid">
            {theaters.map((theater) => (
              <div 
                key={theater._id} 
                className="theater-card"
                onClick={() => handleTheaterSelect(theater.name)}
              >
                <h3 className="theater-name">{theater.name}</h3>
                <p className="theater-location">{theater.location}</p>
                <button className="select-button">
                  Select
                </button>
              </div>
            ))}
          </div>
          
          <button 
            className="back-button"
            onClick={() => navigate(-1)}
          >
            Back to Movie
          </button>
        </>
      )}
    </div>
  );
};

export default TheaterSelectionPage;
