import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import TheatreSeatGenerator from "./TheatreSeatGenerator";
import standardSeatLayout from "../data/TheatreSeat";  
import '../style/TheatreLayout.css';

const TheatreLayout = () => {
  const location = useLocation();
  const navigate = useNavigate();

  // Load theatre data from location state or localStorage
  const [theatreData, setTheatreData] = useState(() => {
    return location.state?.theatreData || JSON.parse(localStorage.getItem("theatreData"));
  });

  useEffect(() => {
    if (location.state?.theatreData) {
      localStorage.setItem("theatreData", JSON.stringify(location.state.theatreData));
    }
  }, [location.state]);

  // Track selected seats
  const [selectedSeats, setSelectedSeats] = useState([]);

  // Function to handle seat selection
  const handleSeatSelect = (seatNumber) => {
    setSelectedSeats((prevSelectedSeats) => {
      if (prevSelectedSeats.includes(seatNumber)) {
        return prevSelectedSeats.filter((seat) => seat !== seatNumber); // Deselect seat
      } else {
        return [...prevSelectedSeats, seatNumber]; // Select seat
      }
    });
  };
  const handlePayment = async()=>{
    try {
      const response = await fetch("http>//localhost:3800/api/book", {
        
      })
    } catch (error) {
      
    }
  }

  // Fallback to default seat layout if data is missing
  const finalTheatreData = theatreData || { theatreName: "Theatre seats", availableSeats: standardSeatLayout, bookedSeats: [] };

  console.log("Final Theatre Data:", finalTheatreData);

  return (
    <div className="theatre-layout">
      <h2>{finalTheatreData.theatreName}</h2>

      <div className="seat-section premium">
        <h3>Premium</h3>
        <TheatreSeatGenerator 
          seatData={finalTheatreData.availableSeats.premium.rowA} 
          rowLabel="A" 
          seatType="premium" 
          bookedSeats={finalTheatreData.bookedSeats}
          selectedSeats={selectedSeats}
          onSeatSelect={handleSeatSelect} 
        />
        <TheatreSeatGenerator 
          seatData={finalTheatreData.availableSeats.premium.rowB} 
          rowLabel="B" 
          seatType="premium" 
          bookedSeats={finalTheatreData.bookedSeats}
          selectedSeats={selectedSeats}
          onSeatSelect={handleSeatSelect} 
        />
      </div>

      <div className="seat-section regular">
        <h3>Regular</h3>
        <TheatreSeatGenerator 
          seatData={finalTheatreData.availableSeats.regular.rowC} 
          rowLabel="C" 
          seatType="regular" 
          bookedSeats={finalTheatreData.bookedSeats}
          selectedSeats={selectedSeats}
          onSeatSelect={handleSeatSelect} 
        />
        <TheatreSeatGenerator 
          seatData={finalTheatreData.availableSeats.regular.rowD} 
          rowLabel="D" 
          seatType="regular" 
          bookedSeats={finalTheatreData.bookedSeats}
          selectedSeats={selectedSeats}
          onSeatSelect={handleSeatSelect} 
        />
      </div>

      <div className="seat-section recliner">
        <h3>Recliner</h3>
        <TheatreSeatGenerator 
          seatData={finalTheatreData.availableSeats.recliner.rowF} 
          rowLabel="F" 
          seatType="recliner" 
          bookedSeats={finalTheatreData.bookedSeats}
          selectedSeats={selectedSeats}
          onSeatSelect={handleSeatSelect} 
        />
      </div>

      {/* Generate Ticket Button */}
      {selectedSeats.length > 0 && (
        <button className="generate-ticket-btn" onClick={() => navigate("/Ticket", { state: { selectedSeats, theatreData: finalTheatreData } })}>
          Pay
        </button>
      )}
    </div>
  );
};

export default TheatreLayout;
