import React, { useEffect, useState } from 'react';
import { useTheatreList } from '../hooks/controller';

const EditTheatre = () => {
    const [theatreName, setTheatreName] = useState('');
    const [location, setLocation] = useState('');
    const [seatCapacity, setSeatCapacity] = useState('');
    const [availableSeats, setAvailableSeats] = useState([]);
    const [movies, setMovies] = useState('');

    const { EditTheatre, editTheatre } = useTheatreList();

    const handleEditTheatre = () => {
        const theatreData = { theatreName, location, seatCapacity, availableSeats, movies };
        EditTheatre(theatreData);
    };

    const generateSeatsArray = (num) => {
        let arr = [];
        for (let i = 1; i <= num; i++) {
            arr.push(i);
        }
        return arr;
    };

    useEffect(() => {
        setTheatreName(editTheatre.theatreName);
        setLocation(editTheatre.location);
        setSeatCapacity(editTheatre.seatCapacity);
        setAvailableSeats(editTheatre.availableSeats);
        setMovies(editTheatre.movies);
    }, [editTheatre]);

    return (
        <div className='editTheatre'>
            <div><label>Theatre Name:</label> <input type='text' value={theatreName} onChange={(e) => setTheatreName(e.target.value)} /></div>
            <div><label>Location:</label> <input type='text' value={location} onChange={(e) => setLocation(e.target.value)} /></div>
            <div><label>Seat Capacity:</label> 
                <input type='number' value={seatCapacity} 
                    onChange={(e) => {
                        setSeatCapacity(e.target.value);
                        setAvailableSeats(generateSeatsArray(e.target.value));
                    }} 
                />
            </div>
            <div><label>Movies Playing:</label> <input type='text' value={movies} onChange={(e) => setMovies(e.target.value)} /></div>
            <div><button onClick={handleEditTheatre}>Save</button></div>
        </div>
    );
};

export default EditTheatre;
