import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import "../style/BookTicket.css";

const BookTicket = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { selectedSeats, theatreData } = location.state || {};
  const [ticket, setTicket] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    if (!selectedSeats || !theatreData) {
      navigate("/");
    } else {
      handleBookTicket();
    }
  }, [selectedSeats, theatreData, navigate]);

  const handleBookTicket = async () => {
    setLoading(true);
    setError("");
    
    const user = JSON.parse(localStorage.getItem("user")); // Retrieve user info from localStorage (or context)
  
  // Assuming the movie title is in theatreData (replace this with the actual source)
    const movieTitle = theatreData.selectedMovieTitle || "Default Movie"; // Default value in case it's missing

  if (!user || !movieTitle) {
    setError("User or movie data is missing.");
    setLoading(false);
    return;
  }

    try {
      const response = await axios.post("http://localhost:3800/api/book", {
        user: user.name, 
        movieTitle: movieTitle, 
        theatreName: theatreData.theatreName,
        showTime: new Date().toISOString(),
        seatNumbers: selectedSeats,
        totalPrice: selectedSeats.length * 150, 
      });

      setTicket(response.data.data);
    } catch (err) {
      setError(err.response?.data?.message || "Failed to book ticket");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="book-ticket-page">
      {loading ? (
        <p>Booking your ticket...</p>
      ) : error ? (
        <p className="error">{error}</p>
      ) : ticket ? (
        <div className="ticket-details">
          <h2>ðŸŽ‰ Ticket Booked Successfully! ðŸŽŸ</h2>
          <p><strong>Movie:</strong> {ticket.movieTitle}</p>
          <p><strong>Theatre:</strong> {ticket.theatreName}</p>
          <p><strong>Show Time:</strong> {new Date(ticket.showTime).toLocaleString()}</p>
          <p><strong>Seats:</strong> {ticket.seatNumbers.join(", ")}</p>
          <p><strong>Total Price:</strong> â‚¹{ticket.totalPrice}</p>
        </div>
      ) : null}
    </div>
  );
};

export default BookTicket;
