import React from "react";
import { useMovieList } from "../"; // Adjust path as needed

const CreateMovie = () => {
  const movieContext = useMovieList();

  if (!movieContext) {
    throw new Error("useMovieList must be used within a MovieProvider");
  }

  const { movies } = movieContext;

  return (
    <div>
      <h1>Movies List</h1>
      {movies.length > 0 ? (
        movies.map((movie) => <div key={movie._id}>{movie.title}</div>)
      ) : (
        <p>No movies available</p>
      )}
    </div>
  );
};

export default CreateMovie;
