import React, { useState } from 'react';
//import { useMediaQuery } from 'react-responsive';
import { useNavigate } from 'react-router-dom';
import Controller from '../hooks/controller';
import '../style/header.css';
import { RiAccountCircleFill } from "react-icons/ri";

function Header() {  
  //const isMobile = useMediaQuery({ maxWidth: 767 });
  const navigate = useNavigate();
  const [userDetail, admin] = Controller()
  const [hidden, sethidden] = useState("")
  return (
    <div className='header-container'>
      <div className='header-title' onClick={()=>navigate('/home')}>
        <h1>CinemaZone</h1>
      </div>
      <div className='button-container'>
      <div className='headerleftside'>
        {admin?null:<div onClick={()=>navigate('/home')}>home</div>}
        {userDetail?<><button className='account' onClick={()=>hidden?sethidden(false):sethidden(true)}><RiAccountCircleFill size={40}/></button></>:<><div className='button' onClick={()=>navigate('/signup')}>Signup</div>
        <div className='button' onClick={()=>navigate('/Login')}>Login</div></>}
      </div>
      </div>
    </div>
  );
}

export default Header;
