import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import TheatreSeatGenerator from "./TheatreSeatGenerator";
import "../style/TheatreLayout.css";

const TheatreLayout = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const movieTitle = location.state?.movieTitle;

  const [theatreData, setTheatreData] = useState(null);
  //const [selectedSeats, setSelectedSeats] = useState([]);

  useEffect(() => {
    if (movieTitle) {
      fetchTheatreData(movieTitle);
    }
  }, [movieTitle]);

  const fetchTheatreData = async () => {
    try {
      const response = await fetch(`http://localhost:3800/api/theatre/movies/${encodeURIComponent(movieTitle)}`, { method: "GET" });
      const data = await response.json();

      console.log("Fetched Theatre Data:", data); // ✅ Debugging API response

      // ✅ Ensure availableSeats is always present
      const updatedData = {
        ...data,
        availableSeats: data.availableSeats || {
          premium: { rowA: [], rowB: [] },
          regular: { rowC: [], rowD: [] },
          recliner: { rowF: [] },
        },
        bookedSeats: data.bookedSeats || [],
      };

      setTheatreData(updatedData);
    } catch (error) {
      console.error("Error fetching theatre data:", error);
    }
  };

  const [selectedSeats, setSelectedSeats] = useState([]);

  const handleSeatSelect = (seatNumber) => {
    setSelectedSeats((prevSelectedSeats) => {
      if (prevSelectedSeats.includes(seatNumber)) {
        return prevSelectedSeats.filter((seat) => seat !== seatNumber);
      } else {
        return [...prevSelectedSeats, seatNumber];
      }
    });
  };

  const handlePayment = async () => {
    try {
      const response = await fetch("http://localhost:3800/api/book", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          userId: "user123",
          movieTitle: theatreData.movieTitle,
          theatreName: theatreData.theatreName,
          showTime: theatreData.showTime,
          seatNumbers: selectedSeats,
          totalPrice: selectedSeats.length * 200,
        }),
      });

      if (!response.ok) {
        throw new Error("Failed to generate ticket");
      }

      const ticketData = await response.json();
      navigate("/Ticket", { state: { ticket: ticketData } });
    } catch (error) {
      console.error("Payment error:", error);
    }
  };

  return (
    <div className="theatre-layout">
      <h2>{theatreData?.theatreName}</h2>

      {theatreData?.availableSeats ? (
        <>
          <div className="seat-section premium">
            <h3>Premium</h3>
            <TheatreSeatGenerator
              seatData={theatreData.availableSeats.premium?.rowA || []}
              rowLabel="A"
              seatType="premium"
              bookedSeats={theatreData.bookedSeats || []}
              selectedSeats={selectedSeats}
              onSeatSelect={handleSeatSelect}
            />
            <TheatreSeatGenerator
              seatData={theatreData.availableSeats.premium?.rowB || []}
              rowLabel="B"
              seatType="premium"
              bookedSeats={theatreData.bookedSeats || []}
              selectedSeats={selectedSeats}
              onSeatSelect={handleSeatSelect}
            />
          </div>

          <div className="seat-section regular">
            <h3>Regular</h3>
            <TheatreSeatGenerator
              seatData={theatreData.availableSeats.regular?.rowC || []}
              rowLabel="C"
              seatType="regular"
              bookedSeats={theatreData.bookedSeats || []}
              selectedSeats={selectedSeats}
              onSeatSelect={handleSeatSelect}
            />
            <TheatreSeatGenerator
              seatData={theatreData.availableSeats.regular?.rowD || []}
              rowLabel="D"
              seatType="regular"
              bookedSeats={theatreData.bookedSeats || []}
              selectedSeats={selectedSeats}
              onSeatSelect={handleSeatSelect}
            />
            <TheatreSeatGenerator
              seatData={theatreData.availableSeats.regular?.rowD || []}
              rowLabel="E"
              seatType="regular"
              bookedSeats={theatreData.bookedSeats || []}
              selectedSeats={selectedSeats}
              onSeatSelect={handleSeatSelect}
            />
          </div>

          <div className="seat-section recliner">
            <h3>Recliner</h3>
            <TheatreSeatGenerator
              seatData={theatreData.availableSeats.recliner?.rowF || []}
              rowLabel="F"
              seatType="recliner"
              bookedSeats={theatreData.bookedSeats || []}
              selectedSeats={selectedSeats}
              onSeatSelect={handleSeatSelect}
            />
          </div>

          {selectedSeats.length > 0 && (
            <button className="generate-ticket-btn" onClick={handlePayment}>
              Pay
            </button>
          )}
        </>
      ) : (
        <p>Loading seats...</p>
      )}
    </div>
  );
};

export default TheatreLayout;
