import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import './TheaterSelectionPage.css'; // Import the CSS file

const TheaterSelectionPage = () => {
  const [theaters, setTheaters] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { movieName } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTheaters = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`/api/theaters/movie/${encodeURIComponent(movieName)}`);
        setTheaters(response.data.theatres);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching theaters:', err);
        setError('Failed to load theaters. Please try again.');
        setLoading(false);
      }
    };

    if (movieName) {
      fetchTheaters();
    }
  }, [movieName]);

  const handleTheaterSelect = (theaterName) => {
    navigate(`/booking/${encodeURIComponent(movieName)}/${encodeURIComponent(theaterName)}`);
  };

  if (loading) {
    return <div className="loading-text">Loading theaters...</div>;
  }

  if (error) {
    return <div className="error-text">{error}</div>;
  }

  if (theaters.length === 0) {
    return (
      <div className="no-theaters">
        <h2>No theaters available for "{movieName}"</h2>
        <button onClick={() => navigate(-1)} className="back-button">
          Go Back
        </button>
      </div>
    );
  }

  return (
    <div className="container">
      <h2 className="page-title">Select a Theater for "{movieName}"</h2>

      <div className="theater-grid">
        {theaters.map((theater) => (
          <div key={theater._id} className="theater-card" onClick={() => handleTheaterSelect(theater.name)}>
            <h3 className="theater-name">{theater.name}</h3>
            <p className="theater-location">{theater.location}</p>
            <button
              className="select-button"
              onClick={(e) => {
                e.stopPropagation();
                handleTheaterSelect(theater.name);
              }}
            >
              Select Theater
            </button>
          </div>
        ))}
      </div>

      <div className="back-container">
        <button onClick={() => navigate(-1)} className="back-button">
          Back to Movie Details
        </button>
      </div>
    </div>
  );
};

export default TheaterSelectionPage;
