import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "../style/TheatreSelect.css";

const TheatreSelect = () => {
  const { title } = useParams();
  const navigate = useNavigate();
  const [theatres, setTheatres] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchTheatres = async () => {
      try {
        const decodedTitle = decodeURIComponent(title);
        console.log("Fetching theatres for:", decodedTitle);

        const response = await fetch(`http://localhost:3800/api/movies/${decodedTitle}/theatres`);

        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const data = await response.json();
        console.log("Fetched Theatres:", data);

        setTheatres(data.theatres || []);
      } catch (err) {
        console.error("Error fetching theatres:", err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchTheatres();
  }, [title]);

  // Navigate to seat selection page when a showtime is clicked
  const handleShowtimeClick = (theatreName, showtime) => {
    navigate(`/seats`, { state: { theatreName, showtime, movieTitle: title } });
  };

  return (
    <div className="theatre-container">
      <h2>Available Theatres for {decodeURIComponent(title)}</h2>

      {loading && <p>Loading theatres...</p>}
      {error && <p>Error: {error}</p>}
      {!loading && !error && theatres.length === 0 && <p>No theatres found.</p>}
      
      {!loading && !error &&
        theatres.map((theatre, index) => (
          <div key={index} className="theatre-box">
            <div className="theatre-info">
              <h3 className="theatre-name">{theatre.name}</h3>
              <p className="theatre-location">{theatre.location}</p>
            </div>
            <div className="showtimes">
              {theatre.showTimes.map((showtime, idx) => (
                <button
                  key={idx}
                  className="showtime-btn"
                  onClick={() => handleShowtimeClick(theatre.name, showtime)}
                >
                  {showtime}
                </button>
              ))}
            </div>
          </div>
        ))
      }
    </div>
  );
};

export default TheatreSelect;
