import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";

const TheatreList = () => {
  const { movieTitle } = useParams();
  const navigate = useNavigate();
  const [theatres, setTheatres] = useState([]);
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5000/api/movies/${encodeURIComponent(movieTitle)}`)
      .then((res) => res.json())
      .then((data) => {
        setMovie(data.movie); // Store movie details
        setTheatres(data.theatres); // Store theatre details
      })
      .catch((err) => console.error("Error fetching data:", err));
  }, [movieTitle]);

  const handleSelectSeats = (theatreName) => {
    navigate(`/seats/${encodeURIComponent(theatreName)}`);
  };

  return (
    <div>
      <h2>Theatres for {movie?.title}</h2>
      {theatres.length > 0 ? (
        theatres.map((theatre) => (
          <div key={theatre.name} className="theatre-card">
            <h3>{theatre.name}</h3>
            <p><strong>Location:</strong> {theatre.location}</p>
            <p><strong>Showtime:</strong> {theatre.showtime}</p>
            <button onClick={() => handleSelectSeats(theatre.name)}>Select Seat</button>
          </div>
        ))
      ) : (
        <p>No theatres available.</p>
      )}
    </div>
  );
};

export default TheatreList;
