import React from "react";
import { useLocation } from "react-router-dom";
import TheatreSeatGenerator from "../components/TheatreSeatGenerator";

const TheatreLayout = () => {
  const location = useLocation();
  const  {theatreData } = location.state || {};

  if (!theatreData || !theatreData.availableSeats) {
    return <p style={{ color: "red" }}>Error: Theatre data is missing!</p>;
  }

  console.log("Theatre Data:", theatreData);
  console.log("Available Seats:", theatreData.availableSeats);
  console.log("Booked Seats:", theatreData.bookedSeats);

  return (
    <div className="theatre-layout">
      <h2>{theatreData.theatreName}</h2>

      {/* âœ… Ensure seat data is passed correctly */}
      <div className="seat-section premium">
        <h3>Premium</h3>
        <TheatreSeatGenerator 
          seatData={theatreData.availableSeats.premium?.rowA || []} 
          rowLabel="A" 
          seatType="premium" 
          bookedSeats={theatreData.bookedSeats || []} />
        <TheatreSeatGenerator 
          seatData={theatreData.availableSeats.premium?.rowB || []} 
          rowLabel="B" 
          seatType="premium" 
          bookedSeats={theatreData.bookedSeats || []} />
      </div>

      <div className="seat-section regular">
        <h3>Regular</h3>
        <TheatreSeatGenerator 
          seatData={theatreData.availableSeats.regular?.rowC || []} 
          rowLabel="C" 
          seatType="regular" 
          bookedSeats={theatreData.bookedSeats || []} />
        <TheatreSeatGenerator 
          seatData={theatreData.availableSeats.regular?.rowD || []} 
          rowLabel="D" 
          seatType="regular" 
          bookedSeats={theatreData.bookedSeats || []} />
      </div>

      <div className="seat-section recliner">
        <h3>Recliner</h3>
        <TheatreSeatGenerator 
          seatData={theatreData.availableSeats.recliner?.rowF || []} 
          rowLabel="F" 
          seatType="recliner" 
          bookedSeats={theatreData.bookedSeats || []} />
      </div>
    </div>
  );
};

export default TheatreLayout;
