import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import "../style/BookTicket.css";

const BookTicket = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { selectedSeats, theatreData, movieData } = location.state || {}; // Get movieData passed from previous page
  const [ticket, setTicket] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // Retrieve user data (e.g., from localStorage or context)
  const user = JSON.parse(localStorage.getItem("user")); // You may replace this with actual authentication state

  useEffect(() => {
    if (!selectedSeats || !theatreData || !movieData || !user) {
      navigate("/"); // Redirect to homepage if any necessary data is missing
    } else {
      handleBookTicket();
    }
  }, [selectedSeats, theatreData, movieData, user, navigate]);

  const handleBookTicket = async () => {
    setLoading(true);
    setError("");

    try {
      const response = await axios.post("http://localhost:3800/api/book", {
        user: user.name, // Use the actual user data
        movieTitle: movieData.title, // Use the movie title passed from the previous page
        theatreName: theatreData.theatreName,
        showTime: new Date().toISOString(),
        seatNumbers: selectedSeats,
        totalPrice: selectedSeats.length * 150, // Assume â‚¹150 per seat
      });

      setTicket(response.data.data);
    } catch (err) {
      setError(err.response?.data?.message || "Failed to book ticket");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="book-ticket-page">
      {loading ? (
        <p>Booking your ticket...</p>
      ) : error ? (
        <p className="error">{error}</p>
      ) : ticket ? (
        <div className="ticket-details">
          <h2>ðŸŽ‰ Ticket Booked Successfully! ðŸŽŸ</h2>
          <p><strong>Movie:</strong> {ticket.movieTitle}</p>
          <p><strong>Theatre:</strong> {ticket.theatreName}</p>
          <p><strong>Show Time:</strong> {new Date(ticket.showTime).toLocaleString()}</p>
          <p><strong>Seats:</strong> {ticket.seatNumbers.join(", ")}</p>
          <p><strong>Total Price:</strong> â‚¹{ticket.totalPrice}</p>
        </div>
      ) : null}
    </div>
  );
};

export default BookTicket;
