import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function CreateMovie() {
  const navigate = useNavigate();
  const [movie, setMovie] = useState({
    title: '',
    genre: '',
    duration: '',
    language: '',
    releaseDate: '',
    rating: '',
    theatre: ''
  });

  const handleChange = (e) => {
    setMovie({ ...movie, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3800/api/movies/create', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(movie),
      });
      const data = await response.json();
      console.log("Movie added:", data);
      navigate('/adminhome'); // Go back to admin home after adding
    } catch (error) {
      console.error("Error adding movie:", error);
    }
  };

  return (
    <div>
      <h2>Create New Movie</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="title" placeholder="Title" onChange={handleChange} required />
        <input type="text" name="genre" placeholder="Genre" onChange={handleChange} required />
        <input type="text" name="duration" placeholder="Duration" onChange={handleChange} required />
        <input type="text" name="language" placeholder="Language" onChange={handleChange} required />
        <input type="date" name="releaseDate" onChange={handleChange} required />
        <input type="number" name="rating" placeholder="Rating" onChange={handleChange} required />
        <input type="text" name="theatre" placeholder="Theatre" onChange={handleChange} required />
        <button type="submit">Add Movie</button>
      </form>
    </div>
  );
}

export default CreateMovie;
