import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import "../style/CreateMovies.css";

function CreateMovie() {
  const navigate = useNavigate();
  const [movie, setMovie] = useState({
    posterURL: '',
    title: '',
    description: '',
    genre: '',
    releaseDate: '',
    director: '',
    cast: '',
    rating: '',
    duration: '',
    language: '',
    theatres: [],
  });

  const [theatreName, setTheatreName] = useState('');
  const [theatreLocation, setTheatreLocation] = useState('');
  const [showTimes, setShowTimes] = useState('');

  const handleChange = (e) => {
    setMovie({ 
      ...movie, 
      [e.target.name]: 
        e.target.name === "duration" || e.target.name === "rating"
        ? Number(e.target.value) || ''  // Convert duration and rating to numbers
        : e.target.value 
    });
  };

  const handleAddTheatre = () => {
    if (!theatreName.trim() || !theatreLocation.trim() || !showTimes.trim()) return;

    setMovie({
      ...movie,
      theatres: [
        ...movie.theatres,
        {
          name: theatreName.trim(),
          location: theatreLocation.trim(),
          showTimes: showTimes.split(",").map(time => time.trim()),
        },
      ],
    });

    setTheatreName('');
    setTheatreLocation('');
    setShowTimes('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const formattedMovie = {
      ...movie,
      cast: movie.cast.split(",").map(name => name.trim()), // Convert cast to array
    };

    try {
      const response = await fetch('http://localhost:3800/api/movies/create', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formattedMovie),
      });

      if (!response.ok) throw new Error("Failed to add movie");
      const data = await response.json();
      console.log("Movie added:", data);
      navigate('/adminhome');
      
    } catch (error) {
      console.error("Error adding movie:", error);
    }
  };

  return (
    <div>
      <h2>Create New Movie</h2>
      <form onSubmit={handleSubmit}>
        <input type='text' name='posterURL' placeholder="Poster URL" onChange={handleChange} required />
        <input type="text" name="title" placeholder="Title" onChange={handleChange} required />
        <textarea name="description" placeholder="Description" onChange={handleChange} required />
        <input type="text" name="genre" placeholder="Genre" onChange={handleChange} required />
        <input type="date" name="releaseDate" onChange={handleChange} required />
        <input type="text" name="director" placeholder="Director" onChange={handleChange} required />
        <input type="text" name="cast" placeholder="Cast (comma separated)" onChange={handleChange} required />
        <input type="number" name="rating" placeholder="Rating (0-10)" onChange={handleChange} required />
        <input type="number" name="duration" placeholder="Duration (in minutes)" onChange={handleChange} required />
        <input type="text" name="language" placeholder="Language" onChange={handleChange} required />

        {/* Add Theatre */}
        <div>
          <input type="text" value={theatreName} placeholder="Theatre Name" onChange={(e) => setTheatreName(e.target.value)} />
          <input type="text" value={theatreLocation} placeholder="Theatre Location" onChange={(e) => setTheatreLocation(e.target.value)} />
          <input type="text" value={showTimes} placeholder="Showtimes (comma separated)" onChange={(e) => setShowTimes(e.target.value)} />
          <button type="button" onClick={handleAddTheatre}>Add Theatre</button>
        </div>

        {/* Display Added Theatres */}
        <ul>
          {movie.theatres.map((theatre, index) => (
            <li key={index}>
              <strong>{theatre.name}</strong> ({theatre.location}) - Showtimes: {theatre.showTimes.join(", ")}
            </li>
          ))}
        </ul>

        <button type="submit">Add Movie</button>
      </form>
    </div>
  );
}

export default CreateMovie;
