import React from "react";
import "../style/TheatreLayout.css";

const TheatreSeatGenerator = ({ seatData, rowLabel, seatType, bookedSeats, selectedSeats, onSeatSelect }) => {
  return (
    <div className={`seat-row ${seatType}`}>
      {seatData.map((seatNumber) => {
        const fullSeatNumber = `${rowLabel}${seatNumber}`;
        return (
          <button
            key={fullSeatNumber}
            className={`seat 
              ${bookedSeats.includes(fullSeatNumber) ? "booked" : ""}
              ${selectedSeats.includes(fullSeatNumber) ? "selected" : ""}`
            }
            onClick={() => onSeatSelect(fullSeatNumber)}
            disabled={bookedSeats.includes(fullSeatNumber)}
          >
            {fullSeatNumber}
          </button>
        );
      })}
    </div>
  );
};

export default TheatreSeatGenerator;
