import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const TheatreSelect = () => {
  const { title } = useParams();  // Get the title from the URL
  const decodedTitle = decodeURIComponent(title); // ✅ Decode the title

  const [theatres, setTheatres] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTheatres = async () => {
      try {
        console.log("Fetching theatres for:", decodedTitle); // Debugging log
        const response = await fetch(`http://localhost:3800/api/movies/${decodedTitle}/theatres`);
        console.log("Response status:", response.status); 

        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const data = await response.json();
        console.log("Fetched data:", data); 
        setTheatres(data.theatres);
      } catch (err) {
        console.error("Error fetching theatres:", err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchTheatres();
  }, [decodedTitle]); // ✅ Depend on decodedTitle

  if (loading) return <p>Loading theatres...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div>
      <h2>Available Theatres for {decodedTitle}</h2>
      {theatres.length === 0 ? (
        <p>No theatres found.</p>
      ) : (
        theatres.map((theatre, index) => (
          <div key={index}>
            <h3>{theatre.name}</h3>
          </div>
        ))
      )}
    </div>
  );
};

export default TheatreSelect;
