import React from "react";
import TheatreSeat from "../data/TheatreSeat"; // Import theatre seat data
import TheatreSeatGenerator from "./TheatreSeatGenerator";

const TheatreLayout = ({ theatreData }) => {
  return (
    <div className="theatre-layout">
      <h2>{theatreData.theatreName}</h2>

      <div className="seat-section premium">
        <h3>Premium</h3>
        <TheatreSeatGenerator seatData={TheatreSeat.premium.rowA} rowLabel="A" seatType="premium" bookedSeats={theatreData.bookedSeats?.rowA || []} />
        <TheatreSeatGenerator seatData={TheatreSeat.premium.rowB} rowLabel="B" seatType="premium" bookedSeats={theatreData.bookedSeats?.rowB || []} />
      </div>

      <div className="seat-section regular">
        <h3>Regular</h3>
        <TheatreSeatGenerator seatData={TheatreSeat.regular.rowC} rowLabel="C" seatType="regular" bookedSeats={theatreData.bookedSeats?.rowC || []} />
        <TheatreSeatGenerator seatData={TheatreSeat.regular.rowD} rowLabel="D" seatType="regular" bookedSeats={theatreData.bookedSeats?.rowD || []} />
      </div>

      <div className="seat-section recliner">
        <h3>Recliner</h3>
        <TheatreSeatGenerator seatData={TheatreSeat.recliner.rowF} rowLabel="F" seatType="recliner" bookedSeats={theatreData.bookedSeats?.rowF || []} />
      </div>
    </div>
  );
};

export default TheatreLayout;
