import React from "react";
import '../style/movieBanner.css'

const MovieBanner = ({posterURL, title, genre, duration, language})=>{
    return (
        <div className="movie-banner">
            <img src={posterURL} alt={title} className="movie-banner-img"/>
            <div className="overlay">
                <h1 className="movie-title">{title}</h1>
                <p className="movie-info">{genre} | {duration} | {language}</p>
            </div>
        </div>
    )
}
export default MovieBanner
