import React, { useEffect, useState } from "react";
import { useParams, useLocation } from "react-router-dom";

const TheatreList = () => {
  const { movieTitle } = useParams(); // Get movie title from URL
  const [theatres, setTheatres] = useState([]);
  const locaton = useLocation()

  useEffect(() => {
    fetch(`http://localhost:3800/api/${movieTitle}`)
      .then((res) => res.json())
      .then((data) => {
        if (data.theatres) {
          setTheatres(data.theatres);
        } else {
          console.error("No theatres found");
        }
      })
      .catch((err) => console.error("Error fetching theatres:", err));
  }, [movieTitle]);

  return (
    <div>
      <h2>Available Theatres for {movieTitle}</h2>
      
    </div>
  );
};

export default TheatreList;
