import React from "react";
import TheatreSeatGenerator from "../components/TheatreSeatGenerator";
import { useLocation } from "react-router-dom";

const TheatreLayout = () => {
  const location = useLocation();
  const  theatreData  = location.state || {}; // ✅ Extract theatreData safely

  if (!theatreData) {
    return <p style={{ color: "red" }}>Error: Theatre data is missing!</p>;
  }

  return (
    <div className="theatre-layout">
      <h2>{theatreData.theatreName}</h2>

      <div className="seat-section premium">
        <h3>Premium</h3>
        <TheatreSeatGenerator seatData={theatreData.availableSeats?.premium?.rowA || []} rowLabel="A" seatType="premium" bookedSeats={theatreData.bookedSeats?.rowA || []} />
        <TheatreSeatGenerator seatData={theatreData.availableSeats?.premium?.rowB || []} rowLabel="B" seatType="premium" bookedSeats={theatreData.bookedSeats?.rowB || []} />
      </div>

      <div className="seat-section regular">
        <h3>Regular</h3>
        <TheatreSeatGenerator seatData={theatreData.availableSeats?.regular?.rowC || []} rowLabel="C" seatType="regular" bookedSeats={theatreData.bookedSeats?.rowC || []} />
        <TheatreSeatGenerator seatData={theatreData.availableSeats?.regular?.rowD || []} rowLabel="D" seatType="regular" bookedSeats={theatreData.bookedSeats?.rowD || []} />
      </div>

      <div className="seat-section recliner">
        <h3>Recliner</h3>
        <TheatreSeatGenerator seatData={theatreData.availableSeats?.recliner?.rowF || []} rowLabel="F" seatType="recliner" bookedSeats={theatreData.bookedSeats?.rowF || []} />
      </div>
    </div>
  );
};

export default TheatreLayout;
