import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import "../style/BookTicket.css";

const BookTicket = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { selectedSeats, theatreData, movieData } = location.state || {}; // Ensure you're destructuring the passed state

  const [ticket, setTicket] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // Effect to check for missing state data
  useEffect(() => {
    if (!selectedSeats || !theatreData || !movieData) {
      navigate("/");  // If any data is missing, redirect to home
    } else {
      handleBookTicket();  // Otherwise, attempt to book the ticket
    }
  }, [selectedSeats, theatreData, movieData, navigate]);

  const handleBookTicket = async () => {
    setLoading(true);
    setError("");

    try {
      const response = await axios.post("http://localhost:3800/api/book", {
        user: "John Doe", // Replace with actual user data if needed
        movieTitle: movieData.title, // Adjust this to match your movie data
        theatreName: theatreData.theatreName,
        showTime: new Date().toISOString(),
        seatNumbers: selectedSeats,
        totalPrice: selectedSeats.length * 150, // Assume â‚¹150 per seat
      });

      setTicket(response.data.data);
    } catch (err) {
      setError(err.response?.data?.message || "Failed to book ticket");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="book-ticket-page">
      {loading ? (
        <p>Booking your ticket...</p>
      ) : error ? (
        <p className="error">{error}</p>
      ) : ticket ? (
        <div className="ticket-details">
          <h2>ðŸŽ‰ Ticket Booked Successfully! ðŸŽŸ</h2>
          <p><strong>Movie:</strong> {ticket.movieTitle}</p>
          <p><strong>Theatre:</strong> {ticket.theatreName}</p>
          <p><strong>Show Time:</strong> {new Date(ticket.showTime).toLocaleString()}</p>
          <p><strong>Seats:</strong> {ticket.seatNumbers.join(", ")}</p>
          <p><strong>Total Price:</strong> â‚¹{ticket.totalPrice}</p>
        </div>
      ) : (
        <p>Loading Ticket Details...</p>
      )}
    </div>
  );
};

export default BookTicket;
