import { useState } from "react";

const TheatreSelect = ({ movie }) => {
  const [theatres, setTheatres] = useState([]);
  const [showTheatres, setShowTheatres] = useState(false);

  const handleBookTicket = async () => {
    try {
      const response = await fetch(`/api/movies/${movie.title}/theatres`);
      const data = await response.json();
      setTheatres(data.theatres);
      setShowTheatres(true);
    } catch (error) {
      console.error("Error fetching theatres:", error);
    }
  };

  return (
    <div>
      <button onClick={handleBookTicket}>Book Ticket</button>

      {showTheatres && (
        <div className="theatre-list">
          <h3>Available Theatres</h3>
          <ul>
            {theatres.map((theatre, index) => (
              <li key={index}>
                <strong>{theatre.name}</strong> - {theatre.location} <br />
                Showtimes: {theatre.showTimes.join(", ")}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default TheatreSelect;
