import React, { useEffect, useState } from 'react';
import { useMovieList } from '../hooks/controller';


const EditMovie = () => {
    const [title, setTitle] = useState('');
    const [genre, setGenre] = useState('');
    const [duration, setDuration] = useState('');
    const [language, setLanguage] = useState('');
    const [releaseDate, setReleaseDate] = useState('');
    const [rating, setRating] = useState('');
    const [theatre, setTheatre] = useState('');

    const { EditMovie, editMovie } = useMovieList();

    const handleEditMovie = () => {
        const movieData = { title, genre, duration, language, releaseDate, rating, theatre };
        EditMovie(movieData);
    };

    useEffect(() => {
        setTitle(editMovie.title);
        setGenre(editMovie.genre);
        setDuration(editMovie.duration);
        setLanguage(editMovie.language);
        setReleaseDate(editMovie.releaseDate);
        setRating(editMovie.rating);
        setTheatre(editMovie.theatre);
    }, [editMovie]); // Fix: Add editMovie as a dependency

    return (
        <div className='editMovie'>
            <div><label>Title:</label> <input type='text' value={title} onChange={(e) => setTitle(e.target.value)} /></div>
            <div><label>Genre:</label> <input type='text' value={genre} onChange={(e) => setGenre(e.target.value)} /></div>
            <div><label>Duration:</label> <input type='text' value={duration} onChange={(e) => setDuration(e.target.value)} /></div>
            <div><label>Language:</label> <input type='text' value={language} onChange={(e) => setLanguage(e.target.value)} /></div>
            <div><label>Release Date:</label> <input type='date' value={releaseDate} onChange={(e) => setReleaseDate(e.target.value)} /></div>
            <div><label>Rating:</label> <input type='number' value={rating} onChange={(e) => setRating(e.target.value)} /></div>
            <div><label>Theatre:</label> <input type='text' value={theatre} onChange={(e) => setTheatre(e.target.value)} /></div>
            <div><button onClick={handleEditMovie}>Save</button></div>
        </div>
    );
};

export default EditMovie;
