import { useParams, useNavigate } from "react-router-dom";
import useFetchMovie from "../hooks/useFetchMovie";
import useTheatreSelection from "../hooks/useTheatreSelection";
import MovieBanner from "../components/MovieBanner";
import MovieInfo from "../components/MovieInfo";
import TheatreList from "../components/TheatreList";

const MovieDetails = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const { movie, loading, error } = useFetchMovie(id);
    const { selectedTheatre, selectTheatre, selectShowtime } = useTheatreSelection();

    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error: {error}</p>;

    return (
        <div>
            <MovieBanner
                posterUrl={movie.posterUrl}
                title={movie.title}
                genre={movie.genre}
                duration={movie.duration}
                language={movie.language}
            />
            <MovieInfo description={movie.description} rating={movie.rating} onBook={() => navigate(`/book/${id}`)} />
            <TheatreList theatres={movie.theatres} />
        </div>
    );
};

export default MovieDetails;
