import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import '../style/BookTicket.css';

const BookTicket = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { selectedSeats, theatreData } = location.state || {};
  const [ticket, setTicket] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    if (!selectedSeats || !theatreData) {
      navigate("/");
    }
  }, [selectedSeats, theatreData, navigate]);

  const handleBookTicket = async () => {
    setLoading(true);
    setError("");

    try {
      const response = await axios.post("http://localhost:3800/api/book", {
        user: "John Doe", // Replace with actual user data
        movieTitle: "Vikram", // Replace with actual movie data
        theatreName: theatreData.theatreName,
        showTime: new Date().toISOString(),
        seatNumbers: selectedSeats,
        totalPrice: selectedSeats.length * 150, // Assume â‚¹150 per seat
      });

      setTicket(response.data.data);
    } catch (err) {
      setError(err.response?.data?.message || "Failed to book ticket");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="book-ticket-page">
      <h2>Confirm Your Booking</h2>
      <p><strong>Theatre:</strong> {theatreData?.theatreName}</p>
      <p><strong>Selected Seats:</strong> {selectedSeats?.join(", ")}</p>
      <p><strong>Total Price:</strong> â‚¹{selectedSeats.length * 150}</p>

      {!ticket ? (
        <button onClick={handleBookTicket} disabled={loading}>
          {loading ? "Booking..." : "Book Ticket"}
        </button>
      ) : (
        <div className="ticket-details">
          <h3>ðŸŽŸ Ticket Confirmed!</h3>
          <p><strong>Movie:</strong> {ticket.movieTitle}</p>
          <p><strong>Theatre:</strong> {ticket.theatreName}</p>
          <p><strong>Show Time:</strong> {new Date(ticket.showTime).toLocaleString()}</p>
          <p><strong>Seats:</strong> {ticket.seatNumbers.join(", ")}</p>
          <p><strong>Total Price:</strong> â‚¹{ticket.totalPrice}</p>
          <button onClick={() => navigate("/")}>Go to Home</button>
        </div>
      )}

      {error && <p className="error">{error}</p>}
    </div>
  );
};

export default BookTicket;
