import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { IoMdAdd } from 'react-icons/io';
import { CgTrash } from 'react-icons/cg';
import { MdEditSquare } from 'react-icons/md';
import { RiSearchLine } from 'react-icons/ri';
import { useMovieList } from '../hooks/controller';
import "../../style/Movie.css";

function CreateMovie() {
  const { movies, getMovies, deleteMovie } = useMovieList();
  const navigate = useNavigate();
  const [movieTitle, setMovieTitle] = useState('');
  const [Movies, setMovies] = useState("")

  // Fetch movies on component mount
  useEffect(() => {
    getMovies();
  }, []);

  const handleSearch = async () => {
    if (!movieTitle.trim()) {
      getMovies(); // Reset to all movies if search is empty
      return;
    }
    try {
      const token = localStorage.getItem('usertoken');
      const response = await fetch(`http://localhost:3800/api/movies/name/${movieTitle}`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      const data = await response.json();
      // Update movies state based on search results
      if (data.movies) {
        // Assuming the API returns { movies: [...] }
        // Adjust according to your API's response structure
        setMovies(data.movies);
      }
    } catch (error) {
      console.error("Search error:", error);
    }
  };

  return (
    <div className='movie'>
      <div className='movieHeader'>
        <div className='adsearch'>
          <label htmlFor='movieTitle'>Movie: </label>
          <input
            type='text'
            id='movieTitle'
            placeholder='Movie title'
            value={movieTitle}
            onChange={(e) => setMovieTitle(e.target.value)}
          />
          <button type='submit' className='aSearch' onClick={handleSearch}>
            <RiSearchLine size={30} />
          </button>
        </div>
        <button className='create' onClick={() => navigate('/adminhome/createMovie')}>
          New Movie <IoMdAdd size={20} />
        </button>
      </div>
      {movies && movies.length > 0 ? (
        movies.map((movie) => (
          <div key={movie._id} className='movieItem'>
            <div className='info'>
              <div>{movie.title}</div>
              <div>{movie.genre}</div>
              <div>{movie.duration} mins</div>
              <div>{movie.language}</div>
              <div>{new Date(movie.releaseDate).toLocaleDateString()}</div>
              <div className='row'>
                <button
                  className='edit'
                  onClick={() => navigate(`/adminhome/editMovie/${movie._id}`)}
                >
                  Edit <MdEditSquare size={25} />
                </button>
                <button className='delete' onClick={() => deleteMovie(movie._id)}>
                  Delete <CgTrash size={25} />
                </button>
              </div>
            </div>
          </div>
        ))
      ) : (
        <p>No movies found</p>
      )}
    </div>
  );
}

export default CreateMovie;
