import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import "../style/CreateMovies.css";

function CreateMovie() {
  const navigate = useNavigate();
  const [movie, setMovie] = useState({
    posterURL: '',
    title: '',
    description: '',
    genre: '',
    releaseDate: '',
    director: '',
    cast: '',
    rating: '',
    duration: '',
    language: '',
    theatres: [],
  });

  const [theatre, setTheatre] = useState({
    name: '',
    location: '',
    showTimes: '',
    seatLayout: {
      premium: { rows: ["A", "B"], availableSeats: 0, bookedSeats: [] },
      regular: { rows: ["C", "D", "E"], availableSeats: 0, bookedSeats: [] },
      recliner: { rows: ["F"], availableSeats: 0, bookedSeats: [] }
    },
  });

  const handleChange = (e) => {
    setMovie({
      ...movie,
      [e.target.name]:
        e.target.name === "duration" || e.target.name === "rating"
          ? Number(e.target.value) || ''
          : e.target.value,
    });
  };

  const handleTheatreChange = (e) => {
    setTheatre({
      ...theatre,
      [e.target.name]: e.target.value,
    });
  };

  const handleSeatChange = (category, field, value) => {
    setTheatre({
      ...theatre,
      seatLayout: {
        ...theatre.seatLayout,
        [category]: {
          ...theatre.seatLayout[category],
          [field]: field === "availableSeats" ? Number(value) : value.split(",").map(s => s.trim()),
        },
      },
    });
  };

  const handleAddTheatre = () => {
    if (!theatre.name.trim() || !theatre.location.trim() || !theatre.showTimes.trim()) return;

    setMovie({
      ...movie,
      theatres: [
        ...movie.theatres,
        {
          name: theatre.name.trim(),
          location: theatre.location.trim(),
          showTimes: theatre.showTimes.split(",").map(time => time.trim()),
          seatLayout: theatre.seatLayout,
        },
      ],
    });

    // Reset theatre input fields
    setTheatre({
      name: '',
      location: '',
      showTimes: '',
      seatLayout: {
        premium: { rows: ["A", "B"], availableSeats: 0, bookedSeats: [] },
        regular: { rows: ["C", "D", "E"], availableSeats: 0, bookedSeats: [] },
        recliner: { rows: ["F"], availableSeats: 0, bookedSeats: [] }
      },
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formattedMovie = {
      ...movie,
      cast: movie.cast.split(",").map(name => name.trim()),
    };

    try {
      const response = await fetch('http://localhost:3800/api/movies/create', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formattedMovie),
      });

      if (!response.ok) throw new Error("Failed to add movie");
      const data = await response.json();
      console.log("Movie added:", data);
      navigate('/adminhome');

    } catch (error) {
      console.error("Error adding movie:", error);
    }
  };

  return (
    <div>
      <h2>Create New Movie</h2>
      <form onSubmit={handleSubmit}>
        <input type='text' name='posterURL' placeholder="Poster URL" onChange={handleChange} required />
        <input type="text" name="title" placeholder="Title" onChange={handleChange} required />
        <textarea name="description" placeholder="Description" onChange={handleChange} required />
        <input type="text" name="genre" placeholder="Genre" onChange={handleChange} required />
        <input type="date" name="releaseDate" onChange={handleChange} required />
        <input type="text" name="director" placeholder="Director" onChange={handleChange} required />
        <input type="text" name="cast" placeholder="Cast (comma separated)" onChange={handleChange} required />
        <input type="number" name="rating" placeholder="Rating (0-10)" onChange={handleChange} required />
        <input type="number" name="duration" placeholder="Duration (in minutes)" onChange={handleChange} required />
        <input type="text" name="language" placeholder="Language" onChange={handleChange} required />

        {/* Add Theatre Section */}
        <div>
          <h3>Add Theatre</h3>
          <input type="text" name="name" value={theatre.name} placeholder="Theatre Name" onChange={handleTheatreChange} required />
          <input type="text" name="location" value={theatre.location} placeholder="Theatre Location" onChange={handleTheatreChange} required />
          <input type="text" name="showTimes" value={theatre.showTimes} placeholder="Showtimes (comma separated)" onChange={handleTheatreChange} required />

          {/* Seat Layout */}
          <h4>Seat Layout</h4>

          <div>
            <strong>Premium Seats (Rows: A, B)</strong>
            <input type="number" placeholder="Available Seats" value={theatre.seatLayout.premium.availableSeats} onChange={(e) => handleSeatChange("premium", "availableSeats", e.target.value)} required />
            <input type="text" placeholder="Booked Seats (comma separated)" value={theatre.seatLayout.premium.bookedSeats.join(", ")} onChange={(e) => handleSeatChange("premium", "bookedSeats", e.target.value)} />
          </div>

          <div>
            <strong>Regular Seats (Rows: C, D, E)</strong>
            <input type="number" placeholder="Available Seats" value={theatre.seatLayout.regular.availableSeats} onChange={(e) => handleSeatChange("regular", "availableSeats", e.target.value)} required />
            <input type="text" placeholder="Booked Seats (comma separated)" value={theatre.seatLayout.regular.bookedSeats.join(", ")} onChange={(e) => handleSeatChange("regular", "bookedSeats", e.target.value)} />
          </div>

          <div>
            <strong>Recliner Seats (Row: F)</strong>
            <input type="number" placeholder="Available Seats" value={theatre.seatLayout.recliner.availableSeats} onChange={(e) => handleSeatChange("recliner", "availableSeats", e.target.value)} required />
            <input type="text" placeholder="Booked Seats (comma separated)" value={theatre.seatLayout.recliner.bookedSeats.join(", ")} onChange={(e) => handleSeatChange("recliner", "bookedSeats", e.target.value)} />
          </div>

          <button type="button" onClick={handleAddTheatre}>Add Theatre</button>
        </div>

        {/* Display Added Theatres */}
        <ul>
          {movie.theatres.map((t, index) => (
            <li key={index}>
              <strong>{t.name}</strong> ({t.location}) - Showtimes: {t.showTimes.join(", ")}
              <br />
              Premium: {t.seatLayout.premium.availableSeats} seats | Booked: {t.seatLayout.premium.bookedSeats.join(", ")}
              <br />
              Regular: {t.seatLayout.regular.availableSeats} seats | Booked: {t.seatLayout.regular.bookedSeats.join(", ")}
              <br />
              Recliner: {t.seatLayout.recliner.availableSeats} seats | Booked: {t.seatLayout.recliner.bookedSeats.join(", ")}
            </li>
          ))}
        </ul>

        <button type="submit">Add Movie</button>
      </form>
    </div>
  );
}

export default CreateMovie;
