import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Controller from "../hooks/Controller";
import "../style/login.css";

function Login() {
  const [useremail, setUserEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const { login } = Controller();


  const loginProcess = async (user, password) => {
    if (user === "" || password === "") {
      alert("Fill username/email and password");
      return;
    }

    const data = { email: user, password: password };

    try {
      const response = await login(data);

      if (response.success) {
        if (response.role === "admin") {
          navigate("/adminhome"); // Redirect to admin home
        } else if (response.role === "customer") {
          navigate("/search"); // Redirect to customer search page
        } else {
          alert("Invalid role. Please contact support.");
        }
      } else {
          alert(response.message || "Login failed");
      }
    } catch (error) {
    console.error("Login error:", error);
    alert("An error occurred. Please try again.");
  }
};


  return (
    <div className="loginbody">
      <div className="loginbox">
        <div className="login">
          <h2>Login</h2>
        </div>
        <div className="logindetails">
          <div className="userNameoremail">
            <label>UserEmail: </label>
            <input
              type="text"
              required
              placeholder="Enter userEmail"
              value={useremail}
              onChange={(e) => setUserEmail(e.target.value)}
            />
          </div>
          <div className="loginpassword">
            <label>Login Password:</label>
            <input
              type="password"
              required
              placeholder="Enter Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div className="logsubmit">
            <button className="log" onClick={() => loginProcess(useremail, password)}>
              Submit
            </button>
            <p onClick={() => navigate("/signup")}>Create account</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
