import React, { useEffect, useState } from 'react';
import  Controller from '../hooks/controller';
import { useNavigate } from 'react-router-dom';
import { IoMdAdd } from 'react-icons/io';
import { CgTrash } from 'react-icons/cg';
import { MdEditSquare } from 'react-icons/md';
import { RiSearchLine } from 'react-icons/ri';

function Movie() {
  const { movies, deleteMovie, setEditMovie, searchMovie, getMovies } = Controller();
  const navigate = useNavigate();
  const [movieTitle, setMovieTitle] = useState('');
  

  console.log(movies);

  const search = () => {
    const data = { title: movieTitle };
    searchMovie(data);
  };

  useEffect(() => {
    if (movieTitle.length === 0) {
      getMovies();
    }
  }, [movieTitle]); // Ensure re-fetching when movieTitle changes

  return (
    <div className='movie'>
      <div className='movieHeader'>
        <div className='adsearch'>
          <label htmlFor='movieTitle'>Movie: </label>
          <input
            type='text'
            id='movieTitle'
            placeholder='Movie title'
            value={movieTitle}
            onChange={(e) => setMovieTitle(e.target.value)}
          />
          <button type='submit' className='aSearch' onClick={search}>
            <RiSearchLine size={30} />
          </button>
        </div>
        <button className='create' onClick={() => navigate('/adminhome/createMovie')}>
          New Movie <IoMdAdd size={20} />
        </button>
      </div>
      {movies.length ? (
        movies.map((movie, index) => (
          <div key={index} className='movieItem'>
            <div className='info'>
              <div>{movie.title}</div>
              <div>{movie.genre}</div>
              <div>{movie.duration}</div>
              <div>{movie.language}</div>
              <div>{movie.releaseDate}</div>
              <div>{movie.rating}</div>
              <div>{movie.theatre}</div>

              <div className='row'>
                <button
                  className='edit'
                  onClick={() => {
                    setEditMovie(movie);
                    navigate('/adminhome/editMovie');
                  }}
                >
                  Edit <MdEditSquare size={25} />
                </button>
                <button className='delete' onClick={() => deleteMovie(movie._id)}>
                  Delete <CgTrash size={25} />
                </button>
              </div>
            </div>
          </div>
        ))
      ) : (
        <p>Movie not found</p>
      )}
    </div>
  );
}

export default Movie;
