import React, { useState, useEffect } from "react";
import axios from "axios";
import "../style/Profile.css";

const Profile = ({ userId }) => {
    const [user, setUser] = useState({});

    useEffect(() => {
        axios.get(`/api/users/${userId}`).then(response => {
            setUser(response.data);
            setFormData(response.data);
        });
    }, [userId]);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleUpdate = async () => {
        await axios.put(`/api/users/update/${userId}`, formData);
        alert("Profile updated!");
    };

    return (
        <div className="profile">
            <h2>{user.username}'s Profile</h2>
            <img src={user.profilePicture || "default-profile.png"} alt="Profile" className="profile-pic" />

            <label>Username:</label>
            <input type="text" name="username" value={formData.username} onChange={handleChange} />

            <label>Email:</label>
            <input type="email" name="email" value={formData.email} onChange={handleChange} disabled />

            <label>Profile Picture URL:</label>
            <input type="text" name="profilePicture" value={formData.profilePicture} onChange={handleChange} />

            <label>Bio:</label>
            <textarea name="bio" value={formData.bio} onChange={handleChange} />

            <label>Location:</label>
            <input type="text" name="location" value={formData.location} onChange={handleChange} />

            <label>Role:</label>
            <select name="role" value={formData.role} onChange={handleChange} disabled={user.role !== "admin"}>
                <option value="customer">Customer</option>
                <option value="admin">Admin</option>
            </select>

            <button onClick={handleUpdate}>Update Profile</button>
        </div>
    );
};

export default Profile;
