import React, {useState} from 'react'
import useSearch from '../hooks/controller'
import '../style/search.css'

function Search() {
    const {SearchTheatre, SearchMovie} = useSearch()
    const [searchQuery, setSearchQuery] = useState('')

    const handleTheatreSearch = ()=>{
        if(searchQuery.trim() === ''){
            alert("Enter a theatre name to search!")
            return
        }
        SearchTheatre({theatreName: searchQuery})
    }
    const handleMovieSearch = () =>{
        if(searchQuery.trim() === ''){
            alert("Enter a movie name to search!")
            return
        }
        SearchMovie({title:searchQuery})
    }
    const handleSearch = () => {
        handleMovieSearch();
    };
    
  return (
    <div className='search-container'>
        <h2>Search for Theatre or Movie</h2>
        <input type='text' placeholder='Enter theatre or movie name' value={searchQuery} onChange={(e)=>setSearchQuery(e.target.value)}/>
        <button onClick={handleSearch}>Search</button>
    </div>
  )
}

export default Search
