import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import TheatreSeatGenerator from "./TheatreSeatGenerator";
import standardSeatLayout from "../data/TheatreSeat";  
import "../style/TheatreLayout.css";

const TheatreLayout = () => {
  const location = useLocation();
  const navigate = useNavigate();

  // Load theatre data from location state or localStorage
  const [theatreData, setTheatreData] = useState(() => {
    return location.state?.theatreData || JSON.parse(localStorage.getItem("theatreData"));
  });

  useEffect(() => {
    if (location.state?.theatreData) {
      localStorage.setItem("theatreData", JSON.stringify(location.state.theatreData));
    }
  }, [location.state]);

  // Track selected seats & showtime
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [selectedShowTime, setSelectedShowTime] = useState(null);

  // Handle seat selection
  const handleSeatSelect = (seatNumber) => {
    setSelectedSeats((prevSelectedSeats) =>
      prevSelectedSeats.includes(seatNumber)
        ? prevSelectedSeats.filter((seat) => seat !== seatNumber) // Deselect
        : [...prevSelectedSeats, seatNumber] // Select
    );
  };

  // Handle showtime selection
  const handleShowTimeSelection = (time) => {
    setSelectedShowTime(time);
  };

  // Proceed to booking
  const handleBooking = () => {
    if (selectedSeats.length === 0 || !selectedShowTime) {
      alert("Please select seats and a showtime before booking.");
      return;
    }

    navigate("/BookTicket", {
      state: {
        selectedSeats,
        selectedShowTime,
        theatreData,
      },
    });
  };

  // Fallback to default seat layout if missing
  const finalTheatreData = theatreData || {
    theatreName: "Theatre Seats",
    availableSeats: standardSeatLayout,
    bookedSeats: [],
    showTimes: [], 
  };

  return (
    <div className="theatre-layout">
      <h2>{finalTheatreData.theatreName}</h2>

      {/* Showtimes Section */}
      <h3>Available Showtimes:</h3>
      <div className="showtime-container">
        {finalTheatreData.showTimes.map((time, index) => (
          <button
            key={index}
            className={`showtime-btn ${selectedShowTime === time ? "selected" : ""}`}
            onClick={() => handleShowTimeSelection(time)}
          >
            {time}
          </button>
        ))}
      </div>

      {/* Premium Seats */}
      <div className="seat-section premium">
        <h3>Premium</h3>
        <TheatreSeatGenerator 
          seatData={finalTheatreData.availableSeats.premium.rowA} 
          rowLabel="A" 
          seatType="premium" 
          bookedSeats={finalTheatreData.bookedSeats}
          selectedSeats={selectedSeats}
          onSeatSelect={handleSeatSelect} 
        />
        <TheatreSeatGenerator 
          seatData={finalTheatreData.availableSeats.premium.rowB} 
          rowLabel="B" 
          seatType="premium" 
          bookedSeats={finalTheatreData.bookedSeats}
          selectedSeats={selectedSeats}
          onSeatSelect={handleSeatSelect} 
        />
      </div>

      {/* Regular Seats */}
      <div className="seat-section regular">
        <h3>Regular</h3>
        <TheatreSeatGenerator 
          seatData={finalTheatreData.availableSeats.regular.rowC} 
          rowLabel="C" 
          seatType="regular" 
          bookedSeats={finalTheatreData.bookedSeats}
          selectedSeats={selectedSeats}
          onSeatSelect={handleSeatSelect} 
        />
        <TheatreSeatGenerator 
          seatData={finalTheatreData.availableSeats.regular.rowD} 
          rowLabel="D" 
          seatType="regular" 
          bookedSeats={finalTheatreData.bookedSeats}
          selectedSeats={selectedSeats}
          onSeatSelect={handleSeatSelect} 
        />
      </div>

      {/* Recliner Seats */}
      <div className="seat-section recliner">
        <h3>Recliner</h3>
        <TheatreSeatGenerator 
          seatData={finalTheatreData.availableSeats.recliner.rowF} 
          rowLabel="F" 
          seatType="recliner" 
          bookedSeats={finalTheatreData.bookedSeats}
          selectedSeats={selectedSeats}
          onSeatSelect={handleSeatSelect} 
        />
      </div>

      {/* Generate Ticket Button */}
      {selectedSeats.length > 0 && selectedShowTime && (
        <button className="generate-ticket-btn" onClick={handleBooking}>
          Pay
        </button>
      )}
    </div>
  );
};

export default TheatreLayout;
