import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import TheatreSeatGenerator from "../components/TheatreSeatGenerator";

const TheatreLayout = () => {
  const location = useLocation();

  // Load theatre data from location state or localStorage
  const [theatreData, setTheatreData] = useState(() => {
    return location.state?.theatreData || JSON.parse(localStorage.getItem("theatreData"));
  });

  useEffect(() => {
    if (location.state?.theatreData) {
      localStorage.setItem("theatreData", JSON.stringify(location.state.theatreData));
    }
  }, [location.state]);

  // Debugging logs
  console.log("Full Location Object:", location);
  console.log("Location State:", location.state);
  console.log("Final Theatre Data:", theatreData);

  if (!theatreData || !theatreData.availableSeats) {
    return <p style={{ color: "red" }}>Error: Theatre data is missing!</p>;
  }

  return (
    <div className="theatre-layout">
      <h2>{theatreData.theatreName}</h2>

      <div className="seat-section premium">
        <h3>Premium</h3>
        <TheatreSeatGenerator 
          seatData={theatreData.availableSeats.premium?.rowA || []} 
          rowLabel="A" 
          seatType="premium" 
          bookedSeats={theatreData.bookedSeats || []} />
        <TheatreSeatGenerator 
          seatData={theatreData.availableSeats.premium?.rowB || []} 
          rowLabel="B" 
          seatType="premium" 
          bookedSeats={theatreData.bookedSeats || []} />
      </div>

      <div className="seat-section regular">
        <h3>Regular</h3>
        <TheatreSeatGenerator 
          seatData={theatreData.availableSeats.regular?.rowC || []} 
          rowLabel="C" 
          seatType="regular" 
          bookedSeats={theatreData.bookedSeats || []} />
        <TheatreSeatGenerator 
          seatData={theatreData.availableSeats.regular?.rowD || []} 
          rowLabel="D" 
          seatType="regular" 
          bookedSeats={theatreData.bookedSeats || []} />
      </div>

      <div className="seat-section recliner">
        <h3>Recliner</h3>
        <TheatreSeatGenerator 
          seatData={theatreData.availableSeats.recliner?.rowF || []} 
          rowLabel="F" 
          seatType="recliner" 
          bookedSeats={theatreData.bookedSeats || []} />
      </div>
    </div>
  );
};

export default TheatreLayout;
