import React, { useEffect, useState } from 'react';
import { useMovieList } from '../hooks/controller';

const EditMovie = () => {
    const [title, setTitle] = useState('');
    const [genre, setGenre] = useState('');
    const [duration, setDuration] = useState('');
    const [language, setLanguage] = useState('');
    const [releaseDate, setReleaseDate] = useState('');
    const [rating, setRating] = useState('');
    const [theatre, setTheatre] = useState('');

    const { movies, editMovie, updateMovie } = useMovieList();

    const handleEditMovie = () => {
        const movieData = { title, genre, duration, language, releaseDate, rating, theatre };
        updateMovie(movieData); // Renamed function for clarity
    };

    useEffect(() => {
        if (editMovie) {
            setTitle(editMovie?.title || '');
            setGenre(editMovie?.genre || '');
            setDuration(editMovie?.duration || '');
            setLanguage(editMovie?.language || '');
            setReleaseDate(editMovie?.releaseDate || '');
            setRating(editMovie?.rating || '');
            setTheatre(editMovie?.theatre || '');
        }
    }, [editMovie]);

    return (
        <div className="movie">
            <h2 className="admin-title">Admin Page</h2>

            {movies?.length > 0 ? (
                movies.map((movie, index) => (
                    <div key={index} className="movieItem">
                        <div className="info">
                            <div>üé¨ {movie?.title || "No Title Available"}</div>
                            <div>üé≠ Genre: {movie?.genre || "N/A"}</div>
                            <div>‚è≥ Duration: {movie?.duration || "N/A"}</div>
                            <div>üó£Ô∏è Language: {movie?.language || "N/A"}</div>
                            <div>üìÖ Release: {movie?.releaseDate || "N/A"}</div>
                            <div>‚≠ê Rating: {movie?.rating || "N/A"}</div>
                            <div>üè¢ Theatre: {movie?.theatre || "N/A"}</div>
                        </div>
                    </div>
                ))
            ) : (
                <p>Loading movies...</p>
            )}
        </div>
    );
};

export default EditMovie;
