import { useEffect, useState } from "react";  // ✅ React hooks imported
import { useParams } from "react-router-dom";

const TheatreSelect = () => {
  const { title } = useParams();  // ✅ Extracting movie title from URL
  const [theatres, setTheatres] = useState([]);  // ✅ State to store theatres
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // ✅ useEffect is the hook responsible for fetching theatres
  useEffect(() => {
    const fetchTheatres = async () => {
      try {
        const response = await fetch(`http://localhost:00/api/movies/${title}/theatres`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setTheatres(data.theatres);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchTheatres();
  }, [title]);  // ✅ This runs when `title` changes

  // ✅ UI handling
  if (loading) return <p>Loading theatres...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div>
      <h2>Available Theatres for {title}</h2>
      {theatres.length > 0 ? (
        <ul>
          {theatres.map((theatre, index) => (
            <li key={index}>{theatre}</li>
          ))}
        </ul>
      ) : (
        <p>No theatres available.</p>
      )}
    </div>
  );
};

export default TheatreSelect;
